#include<iostream>
#include<bits/stdc++.h>
using namespace std;

int main(){
    int a;
    float b;
    bool c;
    char d='a';
    long int si;
    short int li;

    cout<<"The number of size int "<<&(a)<<endl;
    cout<<"The number of size float "<<sizeof(b)<<endl;
    cout<<"The number of size boolean "<<sizeof(c)<<endl;
    cout<<"The number of size char "<<sizeof(d)<<endl;
    cout<<"The number of size long int "<<sizeof(si)<<endl;
    cout<<"The number of size short int "<<sizeof(li)<<endl;



    return 0;
}
